import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import Cookies from "js-cookie";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { userData, showMessages, chatData,friends } from "../redux/actions";
import moment from "moment";
import { Icon, InlineIcon } from "@iconify/react";
import more2Fill from "@iconify-icons/ri/more-2-fill";
import checkboxCircle from "@iconify-icons/ri/checkbox-circle-line";
import forbidLine from "@iconify-icons/ri/forbid-line";
import closeCircle from "@iconify-icons/ri/close-circle-line";

export default function RecentConversation(props) {
  const [friendRequests, setFriendRequests] = useState([]);
  const [friendRequestsDropDown, setFriendRequestsDropDown] = useState(false);
  const [openChats, setOpenChats] = useState(false);

  const dispatch = useDispatch();
  const allFriend = useSelector((state) => state.friends);
  const chatDataState = useSelector((state) => state.chatData);

  console.log(friends);

  const openConversation = (userId) => {
    setOpenChats(!openChats);
    axios
      .get("http://localhost:3005/chats?userId=" + userId)
      .then(function (response) {
        // handle success
        dispatch(showMessages(response.data.info.messages));
        dispatch(chatData(response.data.info));
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      });
  };

  const openFriendRequestsDropDown = () => {
    setFriendRequestsDropDown(true);
  };

  const closeFriendRequestsDropDown = () => {
    setFriendRequestsDropDown(false);
  };

  //   useEffect(() => {
  //     axios.defaults.withCredentials = true;
  //     axios
  //       .get("http://localhost:3005/friendRequests", {
  //         withCredentals: true,
  //       })
  //       .then((res) => {
  //         console.log(res.data.info);
  //         setFriendRequests(res.data.info);
  //       })
  //       .catch((e) => console.log(e));
  //   }, []);

  const acceptFriend = (id) => {
    axios.defaults.withCredentials = true;
    axios
      .get("http://localhost:3005/acceptFriend?id=" + id, {
        withCredentals: true,
      })
      .then((res) => {
        console.log(res);
      })
      .catch((e) => console.log(e));
  };

  const filteredFriends = friends.filter(
    (res) => res.messages.length > 0
  );
  const friendList = filteredFriends.map((res, key) => (
    <li key={key}>
      <a onClick={() => openConversation(res.friendUserId)}>
        <div className="d-flex">
          <div className="chat-user-img online align-self-center me-3 ms-0">
            <img
              src={res.friendInfo.profileImg}
              className="rounded-circle avatar-xs"
              alt=""
            />
            <span className="user-status" />
          </div>
          <div className="flex-1 overflow-hidden">
            <h5 className="text-truncate font-size-15 mb-1">
              {res.friendName}
            </h5>
            <p className="chat-user-message text-truncate mb-0">
              {res.messages[0].message}
            </p>
          </div>
          <div className="font-size-11">
            {moment(res.messages[0].createdAt).startOf("hour").fromNow()}
          </div>
        </div>
      </a>
    </li>
  ));
  const requests =
    friendRequests.length > 0
      ? friendRequests.map((res, key) => {
          return (
            <li key={key}>
              <a>
                <div className="d-flex">
                  <div className="chat-user-img online align-self-center me-3 ms-0">
                    <img
                      src={res.inviter.profileImg}
                      className="rounded-circle avatar-xs"
                      alt=""
                    />
                    <span className="user-status" />
                  </div>
                  <div className="flex-1 overflow-hidden">
                    <h5 className="text-truncate font-size-15 mb-1">
                      {res.inviter.name}
                    </h5>
                    <p className="chat-user-message text-truncate mb-0">
                      hello world
                    </p>
                  </div>
                  <div className="font-size-11 d-flex align-items-center">
                    {moment(res.inviter.createdAt).startOf("hour").fromNow()}
                  </div>
                  <div>
                    <div className="dropdown">
                      <a
                        className="text-muted dropdown-toggle"
                        data-bs-toggle="dropdown"
                        aria-haspopup="true"
                        aria-expanded="false"
                        onClick={openFriendRequestsDropDown}
                      >
                        <Icon icon={more2Fill} />
                      </a>
                      {friendRequestsDropDown ? (
                        <div className="dropdown-menu dropdown-menu-end d-block">
                          <a
                            className="dropdown-item"
                            href="#"
                            onClick={acceptFriend(res.id)}
                          >
                            Accept{" "}
                            <Icon
                              icon={checkboxCircle}
                              className=" float-end text-muted"
                            />
                          </a>
                          <a className="dropdown-item" href="#">
                            Reject{" "}
                            <Icon
                              icon={closeCircle}
                              className=" float-end text-muted"
                            />
                          </a>
                          <a className="dropdown-item" href="#">
                            Block{" "}
                            <Icon
                              icon={forbidLine}
                              className=" float-end text-muted"
                            />
                          </a>
                        </div>
                      ) : (
                        false
                      )}
                    </div>
                  </div>
                </div>
              </a>
            </li>
          );
        })
      : false;

  return (
    <>
      {/* Start chats content */}
      <div>
        <div className="px-4 pt-4">
          <h4 className="mb-4">Chats</h4>
          <div className="search-box chat-search-box">
            <div className="input-group mb-3 rounded-3">
              <span
                className="input-group-text text-muted bg-light pe-1 ps-3"
                id="basic-addon1"
              >
                <i className="ri-search-line search-icon font-size-18" />
              </span>
              <input
                type="text"
                className="form-control bg-light"
                placeholder="Search messages or users"
                aria-label="Search messages or users"
                aria-describedby="basic-addon1"
              />
            </div>
          </div>{" "}
          {/* Search Box*/}
        </div>{" "}
        {/* .p-4 */}
        {/* Start chat-message-list */}
        <ul className="list-unstyled chat-list chat-user-list">{requests}</ul>
        <div className="px-2">
          <h5 className="mb-3 px-3 font-size-16">Recent</h5>
          <div className="chat-message-list" data-simplebar="init">
            <div className="simplebar-wrapper" style={{ margin: "0px" }}>
              <div className="simplebar-height-auto-observer-wrapper">
                <div className="simplebar-height-auto-observer" />
              </div>
              <div className="simplebar-mask">
                <div
                  className="simplebar-offset"
                  style={{ right: "-20px", bottom: "0px" }}
                >
                  <div
                    className="simplebar-content-wrapper"
                    style={{
                      height: "100%",
                      paddingRight: "20px",
                      paddingBottom: "0px",
                      overflow: "hidden scroll",
                    }}
                  >
                    <div
                      className="simplebar-content"
                      style={{ padding: "0px" }}
                    >
                      {/* <ul className="list-unstyled chat-list chat-user-list">
                        {friendList.length > 0 ? (
                          friendList
                        ) : (
                          <li>
                            <div className="d-flex justify-content-center">
                              No recent chat
                            </div>
                            <div className="d-flex justify-content-center mt-3">
