import { ONLINEUSERS } from "../constants/action-types";

const initialState = {
  onlineUsers: [],
};
const onlineUsersReducer = (state = initialState, action) => {
  switch (action.type) {
    case ONLINEUSERS:
      return { ...state, onlineUsers: [...state.onlineUsers, action.payload] };
    default:
      return state;
  }
};

export default onlineUsersReducer;
