import { io } from "socket.io-client";
import Cookies from "js-cookie";

const URL = "http://localhost:3005";
const socket = io(
  URL,
  { autoConnect: false },
  {
    query: `session_id=${
      Cookies.get("connect.sid").replace("s:", "").split(".")[0]
    }`,
  },
  { transports: ["websocket"] },
  {
    withCredentials: true,
  }
);

export default socket;
