import { SHOW_MESSAGES } from "../constants/action-types";

const initialState = {
    messages:[]
}
const showMessagesReducer = (state,action)=>{
    switch(action.type){
        case SHOW_MESSAGES:
            return Object.assign({}, state, {
                messages: state.messages.concat(action.payload),
            });
        default :
        return state
    }
}

export default showMessagesReducer
