import React, { useState, useEffect } from "react";
import axios from "axios";

export default function RecentConversation(props) {
  const [friendRequests, setFriendRequests] = useState();
  useEffect(() => {
    axios.defaults.withCredentials = true;
    axios
      .get("http://localhost:3005/friendRequests", {
        withCredentals: true,
      })
      .then((res) => {
        console.log(res.data.info);
        setFriendRequests(res.data.info);
      })
      .catch((e) => console.log(e));
  }, []);
  console.log(friendRequests);
  if (friendRequests != null && friendRequests.length > 0) {
    const requests = friendRequests.map((key, res) => {
      <div key={key} className="px-2">
        <h5 className="mb-3 px-3 font-size-16">Friend Requests</h5>
        <div className="chat-message-list" data-simplebar="init">
          <div className="simplebar-wrapper" style={{ margin: "0px" }}>
            <div className="simplebar-height-auto-observer-wrapper">
              <div className="simplebar-height-auto-observer" />
            </div>
            <div className="simplebar-mask">
              <div
                className="simplebar-offset"
                style={{ right: "-20px", bottom: "0px" }}
              >
                <div
                  className="simplebar-content-wrapper"
                  style={{
                    height: "100%",
                    paddingRight: "20px",
                    paddingBottom: "0px",
                    overflow: "hidden scroll",
                  }}
                >
                  <div className="simplebar-content" style={{ padding: "0px" }}>
                    <ul className="list-unstyled chat-list chat-user-list">
                      <li>
                        <a>
                          <div className="d-flex">
                            <div className="chat-user-img online align-self-center me-3 ms-0">
                              <img
                                src="/profile"
                                className="rounded-circle avatar-xs"
                                alt=""
                              />
                              <span className="user-status" />
                            </div>
                            <div className="flex-1 overflow-hidden">
                              <h5 className="text-truncate font-size-15 mb-1">
                                name
                              </h5>
                              {/* <p className="chat-user-message text-truncate mb-0">
                                  {res.messages[0].message}
                                </p> */}
                            </div>
                            <div className="font-size-11">time</div>
                          </div>
                        </a>
                      </li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
            <div
              className="simplebar-placeholder"
              style={{ width: "auto", height: "890px" }}
            />
          </div>
          <div
            className="simplebar-track simplebar-horizontal"
            style={{ visibility: "hidden" }}
          >
            <div
              className="simplebar-scrollbar"
              style={{
                transform: "translate3d(0px, 0px, 0px)",
                display: "none",
              }}
            />
          </div>
          <div
            className="simplebar-track simplebar-vertical"
            style={{ visibility: "visible" }}
          >
            <div
              className="simplebar-scrollbar"
              style={{
                height: "597px",
                transform: "translate3d(0px, 0px, 0px)",
                display: "block",
              }}
            />
          </div>
        </div>
      </div>;
    });
  }

  return (
    <>
      {/* Start chats content */}
      <div>
        <div className="px-4 pt-4">
          <h4 className="mb-4">Chats</h4>
          <div className="search-box chat-search-box">
            <div className="input-group mb-3 rounded-3">
              <span
                className="input-group-text text-muted bg-light pe-1 ps-3"
                id="basic-addon1"
              >
                <i className="ri-search-line search-icon font-size-18" />
              </span>
              <input
                type="text"
                className="form-control bg-light"
                placeholder="Search messages or users"
                aria-label="Search messages or users"
                aria-describedby="basic-addon1"
              />
            </div>
          </div>{" "}
          {/* Search Box*/}
        </div>{" "}
        {/* .p-4 */}
        {/* Start chat-message-list */}
        {requests}
        <div className="px-2">
          <h5 className="mb-3 px-3 font-size-16">Recent</h5>
          <div className="chat-message-list" data-simplebar="init">
            <div className="simplebar-wrapper" style={{ margin: "0px" }}>
              <div className="simplebar-height-auto-observer-wrapper">
                <div className="simplebar-height-auto-observer" />
              </div>
              <div className="simplebar-mask">
                <div
                  className="simplebar-offset"
                  style={{ right: "-20px", bottom: "0px" }}
                >
                  <div
                    className="simplebar-content-wrapper"
                    style={{
                      height: "100%",
                      paddingRight: "20px",
                      paddingBottom: "0px",
                      overflow: "hidden scroll",
                    }}
                  >
                    <div
                      className="simplebar-content"
                      style={{ padding: "0px" }}
                    >
                      <ul className="list-unstyled chat-list chat-user-list">
                        {props.data.data.friendList.length > 0 ? (
                          props.data.data.friendList
                        ) : (
                          <li>
                            <div className="d-flex justify-content-center">
                              No recent chat
                            </div>
                            <div className="d-flex justify-content-center mt-3">
                              <button
                                type="submit"
                                class="btn btn-primary font-size-16 btn-lg chat-send waves-effect waves-light"
                              >
                                Start conversation
                              </button>
                            </div>
                          </li>
                        )}
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
              <div
                className="simplebar-placeholder"
                style={{ width: "auto", height: "890px" }}
              />
            </div>
            <div
              className="simplebar-track simplebar-horizontal"
              style={{ visibility: "hidden" }}
            >
              <div
                className="simplebar-scrollbar"
                style={{
                  transform: "translate3d(0px, 0px, 0px)",
                  display: "none",
                }}
              />
            </div>
            <div
              className="simplebar-track simplebar-vertical"
              style={{ visibility: "visible" }}
            >
              <div
                className="simplebar-scrollbar"
                style={{
                  height: "597px",
                  transform: "translate3d(0px, 0px, 0px)",
                  display: "block",
                }}
              />
            </div>
          </div>
        </div>
        {/* End chat-message-list */}
      </div>
      {/* Start chats content */}
    </>
  );
}
